import 'package:bmi_calculator/shared/styles/colors.dart';import 'package:flutter/material.dart';class BmiResultScreen extends StatelessWidget {  final bool isMale;  final int result;  final int age;  BmiResultScreen({    required this.age,    required this.isMale,    required this.result,  });  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: scaffoldColor,      appBar: AppBar(        backgroundColor: scaffoldColor,        title: Text('Bmi Result'),        centerTitle: true,      ),      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            Text(              'Gender : ${isMale ? 'male ' : 'female'}',              style: TextStyle(                color: Colors.white,                fontSize: 30.0,                fontWeight: FontWeight.bold,              ),            ),            Text(              'Result : $result',              style: TextStyle(                color: Colors.white,                fontSize: 30.0,                fontWeight: FontWeight.bold,              ),            ),            Text(              'Age : $age',              style: TextStyle(                color: Colors.white,                fontSize: 30.0,                fontWeight: FontWeight.bold,              ),            ),          ],        ),      ),    );  }}