import 'dart:math';import 'package:bmi_calculator/shared/styles/colors.dart';import 'package:bmi_calculator/shared/styles/colors.dart';import 'package:flutter/material.dart';import '../bmi_results/bmi_result.dart';class BmiScreen extends StatefulWidget {  @override  _BmiScreenState createState() => _BmiScreenState();}class _BmiScreenState extends State<BmiScreen> {  bool isMale = true;  double height = 120;  int age = 30;  int weight = 50;  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        elevation: 20.0,        backgroundColor: scaffoldColor,        title: Text('BmiCalculator'),        centerTitle: true,      ),      backgroundColor: scaffoldColor,      body: Column(        children: [          Expanded(            child: Padding(              padding: const EdgeInsets.all(20.0),              child: Row(                children: [                  Expanded(                    child: GestureDetector(                      onTap: () {                        setState(() {                          isMale = true;                        });                      },                      child: Container(                        decoration: BoxDecoration(                          borderRadius: BorderRadiusDirectional.circular(                            20.0,                          ),                          color: isMale ? buttonColor : containerColor,                        ),                        child: Column(                          mainAxisAlignment: MainAxisAlignment.center,                          children: [                            Icon(                              Icons.male,                              size: 100.0,                              color: Colors.white,                            ),                            SizedBox(                              height: 10.0,                            ),                            Text(                              'Male',                              style: TextStyle(                                color: Colors.white,                                fontWeight: FontWeight.w900,                                fontSize: 30.0,                              ),                            ),                          ],                        ),                      ),                    ),                  ),                  SizedBox(                    width: 20.0,                  ),                  Expanded(                    child: GestureDetector(                      onTap: () {                        setState(() {                          isMale = false;                        });                      },                      child: Container(                        decoration: BoxDecoration(                          borderRadius: BorderRadiusDirectional.circular(                            20.0,                          ),                          color: isMale ? containerColor : buttonColor,                        ),                        child: Column(                          mainAxisAlignment: MainAxisAlignment.center,                          children: [                            Icon(                              Icons.female_sharp,                              size: 100.0,                              color: Colors.white,                            ),                            SizedBox(                              height: 10.0,                            ),                            Text(                              'Female',                              style: TextStyle(                                color: Colors.white,                                fontWeight: FontWeight.w900,                                fontSize: 30.0,                              ),                            )                          ],                        ),                      ),                    ),                  ),                ],              ),            ),          ),          Expanded(            child: Padding(              padding: const EdgeInsets.symmetric(horizontal: 20.0),              child: Container(                decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(                    20.0,                  ),                  color: containerColor,                ),                child: Column(                  mainAxisAlignment: MainAxisAlignment.center,                  children: [                    Text(                      'HEIGHT',                      style: TextStyle(                        color: Colors.white,                        fontWeight: FontWeight.w900,                        fontSize: 30.0,                      ),                    ),                    SizedBox(                      height: 5.0,                    ),                    Row(                      crossAxisAlignment: CrossAxisAlignment.baseline,                      textBaseline: TextBaseline.alphabetic,                      mainAxisAlignment: MainAxisAlignment.center,                      children: [                        Text(                          '${height.round()}',                          style: TextStyle(                            color: Colors.white,                            fontWeight: FontWeight.w900,                            fontSize: 40.0,                          ),                        ),                        Text(                          'CM',                          style: TextStyle(                            color: Colors.grey[600],                            fontSize: 20.0,                          ),                        ),                      ],                    ),                    Slider(                      value: height,                      max: 220,                      min: 50,                      onChanged: (value) {                        setState(() {                          height = value;                        });                      },                    ),                  ],                ),              ),            ),          ),          Expanded(            child: Padding(              padding: const EdgeInsets.all(20.0),              child: Row(                children: [                  Expanded(                    child: Container(                      decoration: BoxDecoration(                        borderRadius: BorderRadius.circular(                          20.0,                        ),                        color: containerColor,                      ),                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          Text(                            'AGE',                            style: TextStyle(                              color: Colors.white,                              fontWeight: FontWeight.w900,                              fontSize: 25.0,                            ),                          ),                          Text(                            '$age',                            style: TextStyle(                              color: Colors.white,                              fontWeight: FontWeight.w900,                              fontSize: 40.0,                            ),                          ),                          Row(                            mainAxisAlignment: MainAxisAlignment.center,                            children: [                              FloatingActionButton(                                backgroundColor: buttonColor,                                onPressed: () {                                  setState(() {                                    age--;                                  });                                },                                heroTag: 'age-',                                mini: true,                                child: Icon(                                  Icons.remove,                                ),                              ),                              FloatingActionButton(                                backgroundColor: buttonColor,                                onPressed: () {                                  setState(() {                                    age++;                                  });                                },                                heroTag: 'age+',                                mini: true,                                child: Icon(                                  Icons.add,                                ),                              ),                            ],                          ),                        ],                      ),                    ),                  ),                  SizedBox(                    width: 20.0,                  ),                  Expanded(                    child: Container(                      decoration: BoxDecoration(                        borderRadius: BorderRadius.circular(                          20.0,                        ),                        color: containerColor,                      ),                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          Text(                            'WEIGHT',                            style: TextStyle(                              color: Colors.white,                              fontWeight: FontWeight.w900,                              fontSize: 25.0,                            ),                          ),                          Text(                            '$weight',                            style: TextStyle(                              color: Colors.white,                              fontWeight: FontWeight.w900,                              fontSize: 40.0,                            ),                          ),                          Row(                            mainAxisAlignment: MainAxisAlignment.center,                            children: [                              FloatingActionButton(                                backgroundColor: buttonColor,                                onPressed: () {                                  setState(() {                                    weight--;                                  });                                },                                heroTag: 'weight-',                                mini: true,                                child: Icon(                                  Icons.remove,                                ),                              ),                              FloatingActionButton(                                backgroundColor: buttonColor,                                onPressed: () {                                  setState(() {                                    weight++;                                  });                                },                                heroTag: 'weight+',                                mini: true,                                child: Icon(                                  Icons.add,                                ),                              ),                            ],                          ),                        ],                      ),                    ),                  ),                ],              ),            ),          ),          Container(            width: double.infinity,            color: buttonColor,            child: MaterialButton(              height: 65.0,              onPressed: () {                double result = weight / pow(height / 100, 2);                // double result = widget / pow(height / 100, 2);                Navigator.push(                  context,                  MaterialPageRoute(                    builder: (context) => BmiResultScreen(                      isMale: isMale,                      age: age,                      result: result.round(),                    ),                  ),                );              },              child: Text(                'CALCULATE',                style: TextStyle(                  color: Colors.white,                  fontSize: 20.0,                  fontWeight: FontWeight.bold,                ),              ),            ),          ),        ],      ),    );  }}